import * as React from 'react';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import './styleNabTabs.css'
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        primary: {
            light: '#757ce8',
            main: '#3f50b5',
            dark: '#002884',
            contrastText: '#fff',
        },
        secondary: {
            light: '#ff7961',
            main: '#f44336',
            dark: '#ba000d',
            contrastText: '#000',
        },
    },
});


export default function TabsButton() {
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <Box sx={{ maxWidth: { xs: 330, sm: 455 },bgcolor: 'dark'}}>
            <Tabs
                selectionFollowsFocus
                value={value}
                onChange={handleChange}
                variant="scrollable"
                scrollButtons={false}
                aria-label="scrollable prevent tabs example"
                textColor="secondary"
                indicatorColor="secondary"





            >
                <Tab href={'/'} style={{color:'white'}}  label="Item One" />
                <Tab style={{color:'white'}} label="Item Two" />
                <Tab style={{color:'white'}} label="Item Three" />
                <Tab  label="Item Four" />
            </Tabs>
        </Box>
    );
}
